import React, { useState } from "react";
import "./Workspace.css";

function ProblemDescription({ details }) {
  const [activeTab, setActiveTab] = useState("description");

  const renderDescription = () => (
    <div>
      <div className="desc-title">
        {details.order}. {details.title}
      </div>
      <div className="desc-difficult" style={{ color: "green" }}>
        {details.difficult}
      </div>
      <div className="problem-description">{details.description}</div>
      <div className="problem-examples">
        {details.examples?.map((example, index) => (
          <div className="example-container" key={index}>
            <div className="example-no">Example {index + 1}:</div>
            <div className="example-desc">
              <strong>Input:</strong> {example.input} <br />
              <strong>Output:</strong> {example.output}
              <br />
              <strong>Explanation:</strong> {example.explanation}
            </div>
          </div>
        ))}
      </div>
      <div className="constraints-container">
        <div className="constraints">Constraints:</div>
        {details.constraints?.map((constraint, index) => (
          <li key={index}>{constraint}</li>
        ))}
      </div>
    </div>
  );

  const renderMetrics = () => (
    <div className="metrics-container">
      <div className="metrics-heading">Metrics</div>
      <div className="metric">
        <strong>Runtime:</strong> {details.metrics?.runtime || "N/A"}
      </div>
      <div className="metric">
        <strong>Time Complexity:</strong> {details.metrics?.timeComplexity || "N/A"}
      </div>
      <div className="metric">
        <strong>Space Complexity:</strong> {details.metrics?.spaceComplexity || "N/A"}
      </div>
    </div>
  );

  return (
    <div className="problem-desc-container">
      <div className="problem-desc-tabs">
        <div
          className={`problem-desc-tab ${activeTab === "description" ? "active" : ""}`}
          onClick={() => setActiveTab("description")}
        >
          Description
        </div>
        <div
          className={`problem-desc-tab ${activeTab === "metrics" ? "active" : ""}`}
          onClick={() => setActiveTab("metrics")}
        >
          Metrics
        </div>
      </div>
      {activeTab === "description" ? renderDescription() : renderMetrics()}
    </div>
  );
}

export default ProblemDescription;
