import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import Split from "react-split";
import { toast } from "react-toastify";
import ProblemDescription from "./ProblemDescription";
import CodeEditor from "./CodeEditor";
import TestCases from "./TestCases";

function Workspace() {
  const urlPathname = window.location.pathname;
  const segments = urlPathname.split("/");
  const problemId = segments[segments.length - 1];

  const [details, setDetails] = useState({});
  const [code, setCode] = useState("");
  const [processing, setProcessing] = useState(false);

  const testcases = details.testcases;

  useEffect(() => {
    async function fetchDetails() {
      try {
        const response = await axios.get(
          `http://localhost:6001/problem/${problemId}`
        );
        setDetails(response.data);
      } catch (error) {
        console.log(error);
      }
    }
    fetchDetails();
  }, [problemId]);

  const onChange = (data) => {
    setCode(data);
  };

  const handleCompile = () => {
    setProcessing(true);

    try {
      // Define a function to evaluate code with input and return output
      const runCode = (code, input, target) => {
        // Create a new function with the code and input
        const userFunction = new Function('nums', 'target', `
          // Add the code to solve the problem here
          ${code}
        `);
        
        // Execute the function with the provided input and return the result
        const result = userFunction(input, target);
        return result;
      };

      // Example input and target for "Two Sum"
      const inputArray = [1, 2, 3]; // This is the array of numbers
      const target = 3; // This is the target sum
      const expectedOutput = "0,1"; // Expected output as indices of the numbers adding up to target

      // Run the code and get the result
      const result = runCode(code, inputArray, target);
      
      // Convert result to a string for comparison
      const resultString = result.join(','); // Assuming result is an array of indices

      // Compare result with expected output
      if (resultString.trim() === expectedOutput.trim()) {
        toast.success("Congrats! Test Case Passed");
      } else {
        toast.error("Oops! Output Didn't Match");
      }
    } catch (error) {
      console.error("Error during evaluation:", error);
      toast.error("Error during evaluation. Check console for details.");
    } finally {
      setProcessing(false);
    }
  };

  return (
    <div className="workspace">
      <Split>
        <ProblemDescription details={details} />
        <CodeEditor code={code} onChange={onChange} />
        <TestCases testcases={details.testcases} />
      </Split>
      <button onClick={handleCompile} disabled={processing}>
        {processing ? "Processing..." : "Compile"}
      </button>
    </div>
  );
}

export default Workspace;
