import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";
import Split from "react-split";
import { toast } from "react-toastify";
import ProblemDescription from "./ProblemDescription";
import CodeEditor from "./CodeEditor";
import TestCases from "./TestCases";

function Workspace() {
  const urlPathname = window.location.pathname;
  const segments = urlPathname.split("/");
  const problemId = segments[segments.length - 1];

  const [details, setDetails] = useState({});
  const [code, setCode] = useState("");
  const [processing, setProcessing] = useState(false);
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");

  const testcases = details.testcases;

  useEffect(() => {
    async function fetchDetails() {
      try {
        const response = await axios.get(
          `http://localhost:6001/problem/${problemId}`
        );
        setDetails(response.data);
      } catch (error) {
        console.log(error);
      }
    }
    fetchDetails();
  }, [problemId]);

  const onChange = (data) => {
    setCode(data);
  };

  const handleCompile = async () => {
    setProcessing(true);
  
    try {
      const response = await axios.get('http://localhost:6001/get-code', { code });
  
      if (response.status === 200) {
        const { output } = response.data;
        toast.success("Code executed successfully");
        setOutput(output);
      } else {
        toast.error("Failed to execute code");
      }
    } catch (error) {
      console.error("Error during execution:", error);
  
      if (error.response) {
        // Server responded with a status other than 200 range
        setError(`Server Error: ${error.response.data.message || error.response.statusText}`);
        toast.error(`Server Error: ${error.response.data.message || error.response.statusText}`);
      } else if (error.request) {
        // Request was made but no response received
        setError("No response from server. Please check the server.");
        toast.error("No response from server. Please check the server.");
      } else {
        // Something else happened
        setError(error.message || "An unknown error occurred.");
        toast.error("An unknown error occurred. Check console for details.");
      }
    } finally {
      setProcessing(false);
    }
  };
  




