import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AvailableContests.css";
import Navbar from "../Topbar/Navbar";

const AvailableContests = () => {
  const [contests, setContests] = useState([]);

  useEffect(() => {
    const fetchContests = async () => {
      const response = await axios.get("/contest/available");
      setContests(response.data);
    };
    fetchContests();
  }, []);

  return (
    <div>
      <Navbar />
    <div className="contests-container">
      <h2 className="heading">Available Contests</h2>
      {contests.length === 0 ? (
        <div className="no-contests">
          <h3>Oops! No Contests Available</h3>
          <p>It looks like there are no contests at the moment. Check back later for exciting challenges!</p>
        </div>
      ) : (
        <ul className="contests-list">
          {contests.map((contest) => (
            <li key={contest._id} className="contest-item">
              <h3 className="contest-title">{contest.title}</h3>
              <p><strong>Created by:</strong> {contest.creator.username}</p>
              <p><strong>Time Limit:</strong> {contest.timeLimit} minutes</p>
              <p><strong>Start Time:</strong> {new Date(contest.startTime).toLocaleString()}</p>
              <p><strong>End Time:</strong> {new Date(contest.endTime).toLocaleString()}</p>
              <ul className="questions-list">
                {contest.questions.map((question) => (
                  <li key={question._id} className="question-item">{question.title}</li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      )}
    </div>
    </div>
  );
};

export default AvailableContests;
