// components/CodingPage/CodingPage.jsx

import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams } from "react-router-dom"; // Import useParams
import CodingNavbar from "../CodingNavbar/CodingNavbar";
import Workspace from "../Workspace/Workspace";

const CodingPage = () => {
  const { id } = useParams();
  const [contestDetails, setContestDetails] = useState({});
  const [problemDetails, setProblemDetails] = useState({});

  useEffect(() => {
    // Fetch contest details
    const fetchContestDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:6001/contest/${id}`);
        setContestDetails(response.data);
      } catch (error) {
        console.error("Error fetching contest details:", error);
      }
    };

    // Fetch problem details
    const fetchProblemDetails = async (questionIds) => {
      try {
        const response = await axios.get(`http://localhost:6001/problem/${id}`);
        setProblemDetails(response.data);
      } catch (error) {
        console.error("Error fetching problem details:", error);
      }
    };

    fetchContestDetails();
    fetchProblemDetails();
  }, [id]);

  return (
    <div className="coding-page">
      {/* Pass contest title and time limit to CodingNavbar */}
      <CodingNavbar
        contestTitle={contestDetails.title || "Loading..."}
        timeLimit={contestDetails.timeLimit || 30} // Default time limit is 30 minutes if not provided
      />
      <Workspace problemDetails={problemDetails} />
    </div>
  );
};

export default CodingPage;
