import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "./ContestInstructions.css";

const ContestInstructions = () => {
  const { id } = useParams(); // Get the contest ID from the URL
  const [contest, setContest] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchContest = async () => {
      try {
        const response = await axios.get(`http://localhost:6001/contest/${id}`);
        setContest(response.data);
      } catch (error) {
        console.error("Error fetching contest details:", error);
      }
    };
    fetchContest();
  }, [id]);

  const handleStart = () => {
    // Navigate to the contest timer page
    navigate(`/contest/${id}/start`);
  };

  if (!contest) return <div>Loading...</div>;

  return (
    <div className="contest-instructions">
      <h1>{contest.title}</h1>
      <p><strong>Time Limit:</strong> {contest.timeLimit} minutes</p>
      <p><strong>Start Time:</strong> {new Date(contest.startTime).toLocaleString()}</p>
      <p><strong>End Time:</strong> {new Date(contest.endTime).toLocaleString()}</p>
      <p><strong>Instructions:</strong> [Add contest instructions here]</p>
      <button onClick={handleStart} className="start-button">Start Contest</button>
    </div>
  );
};

export default ContestInstructions;
