import React, { useState, useEffect } from "react";
import axios from "axios";
import "./CreateContest.css";
import Navbar from "../Topbar/Navbar";

const CreateContest = () => {
  const [title, setTitle] = useState("");
  const [questionId, setQuestionId] = useState(""); // Store problem ID as string
  const [timeLimit, setTimeLimit] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [questions, setQuestions] = useState([]); // Store list of questions

  useEffect(() => {
    // Fetch questions from the backend
    const fetchQuestions = async () => {
      try {
        const response = await axios.get("http://localhost:6001/problems"); // Adjust endpoint as needed
        setQuestions(response.data);
      } catch (error) {
        console.error("Error fetching questions:", error);
      }
    };

    fetchQuestions();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const newContest = {
        title,
        questionId, // Store the selected problem ID as a string
        timeLimit,
        startTime,
        endTime,
      };

      await axios.post("http://localhost:6001/contest/create", newContest);
      alert("Contest created successfully!");
    } catch (error) {
      console.error("Error creating contest:", error);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="create-contest-page">
        <h2>Create a New Contest</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Contest Title:</label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Select Problem:</label>
            <select
              value={questionId}
              onChange={(e) => setQuestionId(e.target.value)}
              required
            >
              <option value="" disabled>Select a question</option>
              {questions.map((question) => (
                <option key={question.id} value={question.id}>
                  {question.title}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Time Limit (in minutes):</label>
            <input
              type="number"
              value={timeLimit}
              onChange={(e) => setTimeLimit(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>Start Time:</label>
            <input
              type="datetime-local"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label>End Time:</label>
            <input
              type="datetime-local"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
              required
            />
          </div>
          <button type="submit" className="btn-create-contest">
            Create Contest
          </button>
        </form>
      </div>
    </div>
  );
};

export default CreateContest;
