import React, { useState, useEffect } from "react";
import axios from "axios";
import "./CreateContest.css";
import Navbar from "../Topbar/Navbar";

const CreateContest = () => {
  const [title, setTitle] = useState("");
  const [questions, setQuestions] = useState([]);
  const [selectedQuestions, setSelectedQuestions] = useState([]);
  const [timeLimit, setTimeLimit] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await axios.get("/problemsTable");
      setQuestions(response.data);
    };
    fetchQuestions();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const creatorId = localStorage.getItem("userId");
    const contestData = {
      creatorId,
      title,
      questionIds: selectedQuestions,
      timeLimit,
      startTime,
      endTime,
    };

    try {
      await axios.post("/contest/create", contestData);
      alert("Contest created successfully!");
    } catch (error) {
      alert("Error creating contest");
    }
  };

  return (
    
    <div className="create-contest-container">
      <h2>Create New Contest</h2>
      <form onSubmit={handleSubmit} className="create-contest-form">
        <div className="form-group">
          <label>Contest Title</label>
          <input
            type="text"
            placeholder="Enter contest title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Select Questions</label>
          <select
            multiple
            value={selectedQuestions}
            onChange={(e) =>
              setSelectedQuestions([...e.target.selectedOptions].map((o) => o.value))
            }
            required
          >
            {questions.map((question) => (
              <option key={question._id} value={question._id}>
                {question.title}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Time Limit (minutes)</label>
          <input
            type="number"
            placeholder="Enter time limit"
            value={timeLimit}
            onChange={(e) => setTimeLimit(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>Start Time</label>
          <input
            type="datetime-local"
            value={startTime}
            onChange={(e) => setStartTime(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label>End Time</label>
          <input
            type="datetime-local"
            value={endTime}
            onChange={(e) => setEndTime(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="create-contest-button">
          Create Contest
        </button>
      </form>
    </div>
  );
};

export default CreateContest;
