import React from "react";
import { useNavigate } from "react-router-dom";
import "./ContestInstructions.css"; // Import the CSS for styling

const ContestInstructions = ({ contest, onStart }) => {
  const navigate = useNavigate();

  const handleStart = () => {
    // Navigate to the contest page or execute the onStart function
    onStart();
    navigate(`/contest/${contest._id}`);
  };

  return (
    <div className="instructions-page">
      <h1>{contest.title}</h1>
      <p><strong>Description:</strong> {contest.description}</p>
      <div className="instructions-content">
        <h2>Instructions</h2>
        <p>{contest.instructions}</p>
      </div>
      <button className="start-button" onClick={handleStart}>
        Start
      </button>
    </div>
  );
};

export default ContestInstructions;
