import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "../Topbar/Navbar";
import "./Home.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import TableData from "./TableData";

function Home() {
  const [problems, setProblems] = useState([]);
  const [darkMode, setDarkMode] = useState(true); // Default to dark mode

  useEffect(() => {
    async function fetchProblems() {
      try {
        const response = await axios.get(
          "http://localhost:6001/problemsTable"
        );
        setProblems(response.data.data);
        toast.success("Problems loaded successfully");
      } catch (error) {
        console.log(error);
        toast.error("Failed to load problems data");
      }
    }
    fetchProblems();

    // Apply dark mode class initially
    if (darkMode) {
      document.body.classList.add("dark-mode");
    }

    return () => {
      // Clean up dark mode class on unmount
      document.body.classList.remove("dark-mode");
    };
  }, [darkMode]);

  const handleToggle = () => {
    setDarkMode((prevMode) => !prevMode);
    document.body.classList.toggle("dark-mode");
  };

  return (
    <>
      <Navbar onToggle={handleToggle} />
      <div className={`main-container ${darkMode ? 'dark-mode' : ''}`}>
        <TableData problems={problems} />
      </div>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />
    </>
  );
}

export default Home;
