import React, { useState, useEffect } from "react";
import axios from "axios";

const AvailableContests = () => {
  const [contests, setContests] = useState([]);

  useEffect(() => {
    const fetchContests = async () => {
      const response = await axios.get("/contest/available");
      setContests(response.data);
    };
    fetchContests();
  }, []);

  return (
    <div>
      <h2>Available Contests</h2>
      <ul>
        {contests.map((contest) => (
          <li key={contest._id}>
            <h3>{contest.title}</h3>
            <p>Created by: {contest.creator.username}</p>
            <p>Time Limit: {contest.timeLimit} minutes</p>
            <p>Start Time: {new Date(contest.startTime).toLocaleString()}</p>
            <p>End Time: {new Date(contest.endTime).toLocaleString()}</p>
            <ul>
              {contest.questions.map((question) => (
                <li key={question._id}>{question.title}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AvailableContests;
