import React, { useState } from "react";
import axios from "axios";
import "./AddProblemForm.css";

function AddProblemForm({ onProblemAdded }) {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category,setCategory] = useState("");
  const [difficulty,setDifficulty] = useState("");
  const [testCases, setTestCases] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:6001/addproblem", {
        title,
        description,
        testCases,
      });
      if (response.status === 200) {
        onProblemAdded();
      } else {
        throw new Error("Failed to add problem");
      }
    } catch (error) {
      console.error("Error adding problem:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="add-problem-form">
      <div>
        <label>Title:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>
      </div>
      <div>
        <label>Difficulty:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setDifficulty(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Category:</label>
        <input
          type="text"
          value={title}
          onChange={(e) => setCategory(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Test Cases:</label>
        <textarea
          value={testCases}
          onChange={(e) => setTestCases(e.target.value.split("\n"))}
          required
        ></textarea>
      </div>
      <button type="submit">Add Problem</button>
    </form>
  );
}

export default AddProblemForm;
